mapper.delete:

delete from cdm_trans_detail
where cust_id = #{custId,jdbcType=VARCHAR}
and date_time = #{dateTime,jdbcType=TIMESTAMP}
and tran_id = #{tranId,jdbcType=VARCHAR}

mapper.select:

select cust_id, date_time, tran_id, card_no, tran_type, tran_cat, tran_msg, tran_amount,
tran_cureen, acct_avaibala, act_name, remit_type, opp_actno, opp_cardno, opp_actname,
opp_brname, opp_brno, tran_amountcny, mer_no, mer_name, rembak, usage_name, tran_ps,
summary, sum_flag, edit_flag, sys_code
from cdm_trans_detail
where cust_id = #{custId,jdbcType=VARCHAR}
and date_time = #{dateTime,jdbcType=TIMESTAMP}
and tran_id = #{tranId,jdbcType=VARCHAR}
