mapper.insert:

insert into test_detail_agg (UNN_TRNO, TXN_DT, TXN_ACCNO,
SUMMRY, TRAN_TYPE, TXN_AMT
)
values (#{unnTrno,jdbcType=INTEGER}, #{txnDt,jdbcType=DATE}, #{txnAccno,jdbcType=VARCHAR},
#{summry,jdbcType=VARCHAR}, #{tranType,jdbcType=VARCHAR}, #{txnAmt,jdbcType=INTEGER}
)

mapper.select:

select UNN_TRNO, TXN_DT, TXN_ACCNO, SUMMRY, TRAN_TYPE, TXN_AMT
from test_detail_agg
where UNN_TRNO = #{unnTrno,jdbcType=INTEGER}

    and TXN_DT >= #{lowerDate,jdbcType=DATE}


    and TXN_DT <= #{upperDate,jdbcType=DATE}

select UNN_TRNO, TXN_DT, TXN_ACCNO, SUMMRY, TRAN_TYPE, TXN_AMT
from test_detail_agg
where TXN_ACCNO = #{txnAccno,jdbcType=VARCHAR}

    and TXN_DT >= #{lowerDate,jdbcType=DATE}


    and TXN_DT <= #{upperDate,jdbcType=DATE}

order by TXN_DT desc
limit #{offset,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*)
from test_detail_agg
where TXN_ACCNO = #{txnAccno,jdbcType=VARCHAR}

    and TXN_DT >= #{lowerDate,jdbcType=DATE}


    and TXN_DT <= #{upperDate,jdbcType=DATE}

select SUBSTR(TXN_DT, 1, 4) as year, TRAN_TYPE, SUM(TXN_AMT) as sum
from test_detail_agg
where TXN_ACCNO = #{txnAccno,jdbcType=VARCHAR}

    and TXN_DT >= #{lowerDate,jdbcType=DATE}


    and TXN_DT <= #{upperDate,jdbcType=DATE}

group by SUBSTR(TXN_DT, 1, 4), TRAN_TYPE
